import React, { useState, useRef } from 'react';

import classes from './AddMovie.module.css';

function AddMovie(props) {
 const [enteredTitle, setenteredTitle] = useState('')
 const [enteredOpeningText, setenteredOpeningText] = useState('')
  const [enteredReleaseDate, setenteredReleaseDate] = useState('')

  const titleRef = useRef('');
  const openingTextRef = useRef('');
  const releaseDateRef = useRef('');

  function submitHandler(event) {
    event.preventDefault();

    // could add validation here...

    const movie = {
      title: titleRef.current.value,
      openingText: openingTextRef.current.value,
      releaseDate: releaseDateRef.current.value,
    };

    props.onAddMovie(movie);

    setenteredTitle('');
    setenteredOpeningText('');
    setenteredReleaseDate('');
  }


  return (
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' ref={titleRef} value='enteredTitle' />
      </div>
      <div className={classes.control}>
        <label htmlFor='opening-text'>Opening Text</label>
        <textarea rows='5' id='opening-text' ref={openingTextRef} value='enteredOpeningText'></textarea>
      </div>
      <div className={classes.control}>
        <label htmlFor='date'>Release Date</label>
        <input type='text' id='date' ref={releaseDateRef} value='enteredReleaseDate'/>
      </div>
       <button>Add Movie</button>
      <button onClick={props.onClose} >Cancel</button>
    </form>
  );
}

export default AddMovie;
